mapper.select:
SELECT USER_NAME AS userName
  FROM ballondor.memberlist
 WHERE USER_ID = #{userId}
   AND USER_PW = #{userPw}

SELECT USER_NAME AS userName
     , USER_ID   AS userId
  FROM ballondor.memberlist
 WHERE USER_ID = #{userId}
   AND USER_PW = #{userPw}

SELECT USER_ID AS userId
  FROM ballondor.memberlist
 WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO ballondor.memberlist
(
	USER_ID
,	USER_PW
,	USER_NAME
)
VALUES
(
	#{userId}
,	#{userPw}
,	#{userName}
)
